pragma solidity ^0.8.15;


import "./MarketTradingControl.sol";
import "./ApedToken.sol";
import "./MockDAI.sol";
import "./AccessControl.sol";

interface DAIInterface {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
}

contract Vault {

    //contracts
    TradingStorage immutable tradingStorage;
    MarketTradingControl immutable marketTradingControl;
    ApedToken immutable apedToken;
    AccessControl immutable accessControl;
    MockDAI public DAI;


    uint internal vaultTokenBalance;

    address private apedAdmin;
    address[] public traders;

    constructor(MarketTradingControl _tradingControl, 
                TradingStorage _tradingStorage,
                ApedToken _apedToken, 
                MockDAI _daiAddress, 
                AccessControl _accessControl)  {
        marketTradingControl = _tradingControl;
        apedToken = _apedToken;
        DAI = _daiAddress;
        accessControl = _accessControl;
        tradingStorage = _tradingStorage;

    }



    function openMarketPriceOrder(
                uint _pair, 
                int orderType, 
                int _leverageAmount, 
                int _collateral, 
                int _takeProfit, 
                int _stopLoss) public returns(uint) {

        DAI.transferFrom(msg.sender, address(this), uint(_collateral));

        uint tradeId = marketTradingControl.openMarketOrder(
                         msg.sender, 
                         _pair, 
                         orderType, 
                         _leverageAmount, 
                         _collateral, 
                         _takeProfit,
                          _stopLoss
                    );


        return tradeId;

    }

    // _tradeId - index location in allTrades[]
    // _tradeIndex - index location in userOpenTrades
    function closeOrder(uint _tradeId) public returns(int) {
        int collateral = tradingStorage.getOpenTradeDetails(_tradeId, msg.sender).collateral;

        int profit  = marketTradingControl.closeMarketOrder(_tradeId, msg.sender);


        int pnl = collateral + profit;
        if(pnl > 0) DAI.transfer(msg.sender, uint(pnl));
        // if (profit > 0) {
        //     DAI.transfer(msg.sender, uint(pnl));
        // } else if (profit < 0 && pnl > 0) {
        //     DAI.transfer(msg.sender, uint(pnl));
        // } 

        vaultBalancing();

        return pnl;


    }


    function vaultBalancing() internal {
       uint vaultReserveBalance = DAI.balanceOf(address(this));

       uint curveReserveBalance = DAI.balanceOf(address(apedToken));
       uint burnThreshold = percent(90, curveReserveBalance);
       uint mintThreshold = percent(120, curveReserveBalance);


       if (vaultReserveBalance <= burnThreshold && vaultReserveBalance >= 200000 * 10e18 ) {
        // calculate required APED token amount to get 10% amount of DAI from curve
        uint reserveTokenRequest = percent(10, curveReserveBalance);
        uint apedTokenRequired = apedToken.calculateBurnAmountRequired(reserveTokenRequest);

        // need to check if vault has equal or more aped Token required
        apedToken.burn(apedTokenRequired);

       } else if (vaultReserveBalance >= mintThreshold && vaultReserveBalance >= 200000 * 10e18) {

          uint excessVaultBalance = vaultReserveBalance - (200000 * 10e18);        
          uint token = apedToken.mint(excessVaultBalance);
          vaultTokenBalance = vaultTokenBalance + token;

       }

    }

    function percent(uint a, uint b) internal pure returns(uint) {
      uint c = a*b;
      uint integer = c/100;
    //   uint fractional = c%100;
      return integer;
    }

    function checkTokenVaultBalance() public view returns (uint) {
        require(accessControl.isAdmin(msg.sender));
        return vaultTokenBalance;
    }


}
